mapper.select:
SELECT tc.clue_id,tc.`name`,tch.channel_name,tc.phone,tc.`status`,tc.create_time,
       tc.create_by,ta.user_name as OWNER, tc.next_time
FROM `tienchin_clue` tc LEFT JOIN tienchin_channel tch on tc.channel_id=tch.channel_id
    LEFT JOIN tienchin_assignment ta on tc.clue_id=ta.assign_id AND ta.latest=TRUE where tc.del_flag =0 and ta.type = 0

    and tc.name =#{name}


    and tc.phone =#{phone}


    and tc.channel_id=#{channelId}


    and tc.status=#{status}


    and ta.user_name=#{owner}


    and tc.next_time > #{params.beginTime} and tc.next_time < #{params.endTime}

SELECT t2.date_str as x, if(t3.c is null,0,t3.c) as y FROM ( SELECT @sdate := date_add(date_format(#{params.beginTime},'%Y-%m-%d'), interval 1 day) AS date_str UNION ALL SELECT @sdate := date_add(@sdate, interval 1 day) FROM mysql.time_zone LIMIT 999 ) t2 LEFT JOIN ( SELECT DATE(create_time) AS date_str, COUNT(1) AS c -- 给 COUNT 结果加上别名 FROM tienchin_clue
and channel_id=#{channelId}
and status=#{status}
GROUP BY DATE(create_time)) t3 on t2.date_str=t3.date_str where t2.date_str <= date_add(date_format(#{params.endTime},'%Y-%m-%d'),interval -1 day)

set @cnum:=0; select t4.x,(@cnum:=@cnum+t4.y) as y from( SELECT t2.date_str as x, if(t3.c is null,0,t3.c) as y FROM ( SELECT @sdate := date_add(date_format(#{params.beginTime},'%Y-%m-%d'), interval 1 day) AS date_str UNION ALL SELECT @sdate := date_add(@sdate, interval 1 day) FROM mysql.time_zone LIMIT 999 ) t2 LEFT JOIN ( SELECT DATE(create_time) AS date_str, COUNT(1) AS c -- 给 COUNT 结果加上别名 FROM tienchin_clue
and channel_id=#{channelId}
and status=#{status}
GROUP BY DATE(create_time)) t3 on t2.date_str=t3.date_str where t2.date_str <= date_add(date_format(#{params.endTime},'%Y-%m-%d'),interval -1 day)) t4;
